		if ($criteria === null) {
			$criteria = new Criteria(AfiliacionPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collPoliticoAfiliacions === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(PoliticoAfiliacionPeer::AFILIACION_ID, $this->id);

				$count = PoliticoAfiliacionPeer::doCount($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(PoliticoAfiliacionPeer::AFILIACION_ID, $this->id);

				if (!isset($this->lastPoliticoAfiliacionCriteria) || !$this->lastPoliticoAfiliacionCriteria->equals($criteria)) {
					$count = PoliticoAfiliacionPeer::doCount($criteria, $con);
				} else {
					$count = count($this->collPoliticoAfiliacions);
				}
			} else {
				$count = count($this->collPoliticoAfiliacions);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a PoliticoAfiliacion object to this object
	 * through the PoliticoAfiliacion foreign key attribute.
	 *
	 * @param      PoliticoAfiliacion $l PoliticoAfiliacion
	 * @return     void
	 * @throws     PropelException
	 */
	public function addPoliticoAfiliacion(PoliticoAfiliacion $l)
	{
		if ($this->collPoliticoAfiliacions === null) {
			$this->initPoliticoAfiliacions();
		}
		if (!in_array($l, $this->collPoliticoAfiliacions, true)) { // only add it if the **same** object is not already associated
			array_push($this->collPoliticoAfiliacions, $l);
			$l->setAfiliacion($this);
		}
	}


	/**
	 * If this collection has already been initialized with
	 * an identical criteria, it returns the collection.
	 * Otherwise if this Afiliacion is new, it will return
	 * an empty collection; or if this Afiliacion has previously
	 * been saved, it will retrieve related PoliticoAfiliacions from storage.
	 *
	 * This method is protected by default in order to keep the public
	 * api reasonable.  You can provide public methods for those you
	 * actually need in Afiliacion.
	 */
	public function getPoliticoAfiliacionsJoinPolitico($criteria = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		if ($criteria === null) {
			$criteria = new Criteria(AfiliacionPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collPoliticoAfiliacions === null) {
			if ($this->isNew()) {
				$this->collPoliticoAfiliacions = array();
			} else {

				$criteria->add(PoliticoAfiliacionPeer::AFILIACION_ID, $this->id);

				$this->collPoliticoAfiliacions = PoliticoAfiliacionPeer::doSelectJoinPolitico($criteria, $con, $join_behavior);
			}
		} else {
			// the following code is to determine if a new query is
			// called for.  If the criteria is the same as the last
			// one, just return the collection.

			$criteria->add(PoliticoAfiliacionPeer::AFILIACION_ID, $this->id);

			if (!isset($this->lastPoliticoAfiliacionCriteria) || !$this->lastPoliticoAfiliacionCriteria->equals($criteria)) {
				$this->collPoliticoAfiliacions = PoliticoAfiliacionPeer::doSelectJoinPolitico($criteria, $con, $join_behavior);
			}
		}
		$this->lastPoliticoAfiliacionCriteria = $criteria;

		return $this->collPoliticoAfiliacions;
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collPartidoAfiliacions) {
				foreach ((array) $this->collPartidoAfiliacions as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPoliticoAfiliacions) {
				foreach ((array) $this->collPoliticoAfiliacions as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collPartidoAfiliacions = null;
		$this->collPoliticoAfiliacions = null;
			$this->aPartido = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseAfiliacion:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseAfiliacion::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


