	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collConvocatoriaI18ns) {
				foreach ((array) $this->collConvocatoriaI18ns as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collEnlaces) {
				foreach ((array) $this->collEnlaces as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collListas) {
				foreach ((array) $this->collListas as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collConvocatoriaI18ns = null;
		$this->collEnlaces = null;
		$this->collListas = null;
			$this->aEleccion = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseConvocatoria:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseConvocatoria::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

	// symfony_i18n behavior
	
	/**
	 * Returns the culture.
	 *
	 * @return string The culture
	 */
	public function getCulture()
	{
	  return $this->culture;
	}
	
	/**
	 * Sets the culture.
	 *
	 * @param string  The culture to set
	 *
	 * @return Convocatoria
	 */
	public function setCulture($culture)
	{
	  $this->culture = $culture;
	  return $this;
	}
	
	/**
	 * Returns the "descripcion" value from the current {@link ConvocatoriaI18n}.
	 */
	public function getDescripcion($culture = null)
	{
	  return $this->getCurrentConvocatoriaI18n($culture)->getDescripcion();
	}
	
	/**
	 * Sets the "descripcion" value of the current {@link ConvocatoriaI18n}.
	 *
	 * @return Convocatoria
	 */
	public function setDescripcion($value, $culture = null)
	{
	  $this->getCurrentConvocatoriaI18n($culture)->setDescripcion($value);
	  return $this;
	}
	
	/**
	 * Returns the current translation.
	 *
	 * @return ConvocatoriaI18n
	 */
	public function getCurrentConvocatoriaI18n($culture = null)
	{
	  if (null === $culture)
	  {
	    $culture = null === $this->culture ? sfPropel::getDefaultCulture() : $this->culture;
	  }
	
	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : ConvocatoriaI18nPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->setConvocatoriaI18nForCulture($object, $culture);
	    }
	    else
	    {
	      $this->setConvocatoriaI18nForCulture(new ConvocatoriaI18n(), $culture);
	      $this->current_i18n[$culture]->setCulture($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function setConvocatoriaI18nForCulture(ConvocatoriaI18n $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addConvocatoriaI18n($object);
	}

