	public function setCulture($culture)
	{
	  $this->culture = $culture;
	  return $this;
	}
	
	/**
	 * Returns the "nombre_corto" value from the current {@link EleccionI18n}.
	 */
	public function getNombreCorto($culture = null)
	{
	  return $this->getCurrentEleccionI18n($culture)->getNombreCorto();
	}
	
	/**
	 * Sets the "nombre_corto" value of the current {@link EleccionI18n}.
	 *
	 * @return Eleccion
	 */
	public function setNombreCorto($value, $culture = null)
	{
	  $this->getCurrentEleccionI18n($culture)->setNombreCorto($value);
	  return $this;
	}
	
	/**
	 * Returns the "nombre" value from the current {@link EleccionI18n}.
	 */
	public function getNombre($culture = null)
	{
	  return $this->getCurrentEleccionI18n($culture)->getNombre();
	}
	
	/**
	 * Sets the "nombre" value of the current {@link EleccionI18n}.
	 *
	 * @return Eleccion
	 */
	public function setNombre($value, $culture = null)
	{
	  $this->getCurrentEleccionI18n($culture)->setNombre($value);
	  return $this;
	}
	
	/**
	 * Returns the current translation.
	 *
	 * @return EleccionI18n
	 */
	public function getCurrentEleccionI18n($culture = null)
	{
	  if (null === $culture)
	  {
	    $culture = null === $this->culture ? sfPropel::getDefaultCulture() : $this->culture;
	  }
	
	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : EleccionI18nPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->setEleccionI18nForCulture($object, $culture);
	    }
	    else
	    {
	      $this->setEleccionI18nForCulture(new EleccionI18n(), $culture);
	      $this->current_i18n[$culture]->setCulture($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function setEleccionI18nForCulture(EleccionI18n $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addEleccionI18n($object);
	}

} // BaseEleccion
