					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					PartidoPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (ListaCalle) to the collection in $obj3 (Partido)
				$obj3->addListaCalle($obj1);

			} // if joined row is not null

				// Add objects for joined Politico rows

				$key4 = PoliticoPeer::getPrimaryKeyHashFromRow($row, $startcol4);
				if ($key4 !== null) {
					$obj4 = PoliticoPeer::getInstanceFromPool($key4);
					if (!$obj4) {
	
						$cls = PoliticoPeer::getOMClass(false);

					$obj4 = new $cls();
					$obj4->hydrate($row, $startcol4);
					PoliticoPeer::addInstanceToPool($obj4, $key4);
				} // if $obj4 already loaded

				// Add the $obj1 (ListaCalle) to the collection in $obj4 (Politico)
				$obj4->addListaCalle($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

