				foreach ((array) $this->collEnlaces as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collImagens) {
				foreach ((array) $this->collImagens as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collListas) {
				foreach ((array) $this->collListas as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPartidosRelatedByPartidoId) {
				foreach ((array) $this->collPartidosRelatedByPartidoId as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPartidoI18ns) {
				foreach ((array) $this->collPartidoI18ns as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPartidoListas) {
				foreach ((array) $this->collPartidoListas as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPoliticos) {
				foreach ((array) $this->collPoliticos as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPromocions) {
				foreach ((array) $this->collPromocions as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collEnlaces = null;
		$this->collImagens = null;
		$this->collListas = null;
		$this->collPartidosRelatedByPartidoId = null;
		$this->collPartidoI18ns = null;
		$this->collPartidoListas = null;
		$this->collPoliticos = null;
		$this->collPromocions = null;
			$this->aPartidoRelatedByPartidoId = null;
	}


  /**
   * Returns the culture.
   *
   * @return string The culture
   */
  public function getCulture()
  {
    return $this->culture;
  }

  /**
   * Sets the culture.
   *
   * @param string $culture The culture to set
   *
   * @return void
   */
  public function setCulture($culture)
  {
    $this->culture = $culture;
  }

  public function getNombre($culture = null)
  {
    return $this->getCurrentPartidoI18n($culture)->getNombre();
  }

  public function setNombre($value, $culture = null)
  {
    $this->getCurrentPartidoI18n($culture)->setNombre($value);
  }

  public function getPresentacion($culture = null)
  {
    return $this->getCurrentPartidoI18n($culture)->getPresentacion();
  }

  public function setPresentacion($value, $culture = null)
  {
    $this->getCurrentPartidoI18n($culture)->setPresentacion($value);
  }

  protected $current_i18n = array();

  public function getCurrentPartidoI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = PartidoI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setPartidoI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setPartidoI18nForCulture(new PartidoI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setPartidoI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addPartidoI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BasePartido:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BasePartido::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


} // BasePartido
