	/**
	 * Returns the current translation.
	 *
	 * @return PoliticoI18n
	 */
	public function getCurrentPoliticoI18n($culture = null)
	{
	  if (null === $culture)
	  {
	    $culture = null === $this->culture ? sfPropel::getDefaultCulture() : $this->culture;
	  }
	
	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : PoliticoI18nPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->setPoliticoI18nForCulture($object, $culture);
	    }
	    else
	    {
	      $this->setPoliticoI18nForCulture(new PoliticoI18n(), $culture);
	      $this->current_i18n[$culture]->setCulture($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function setPoliticoI18nForCulture(PoliticoI18n $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addPoliticoI18n($object);
	}

