	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(PropuestaPeer::DATABASE_NAME);

		if ($this->isColumnModified(PropuestaPeer::ID)) $criteria->add(PropuestaPeer::ID, $this->id);
		if ($this->isColumnModified(PropuestaPeer::TITULO)) $criteria->add(PropuestaPeer::TITULO, $this->titulo);
		if ($this->isColumnModified(PropuestaPeer::DESCRIPCION)) $criteria->add(PropuestaPeer::DESCRIPCION, $this->descripcion);
		if ($this->isColumnModified(PropuestaPeer::CULTURE)) $criteria->add(PropuestaPeer::CULTURE, $this->culture);
		if ($this->isColumnModified(PropuestaPeer::IMAGEN)) $criteria->add(PropuestaPeer::IMAGEN, $this->imagen);
		if ($this->isColumnModified(PropuestaPeer::DOC)) $criteria->add(PropuestaPeer::DOC, $this->doc);
		if ($this->isColumnModified(PropuestaPeer::DOC_SIZE)) $criteria->add(PropuestaPeer::DOC_SIZE, $this->doc_size);
		if ($this->isColumnModified(PropuestaPeer::SF_GUARD_USER_ID)) $criteria->add(PropuestaPeer::SF_GUARD_USER_ID, $this->sf_guard_user_id);
		if ($this->isColumnModified(PropuestaPeer::SUMU)) $criteria->add(PropuestaPeer::SUMU, $this->sumu);
		if ($this->isColumnModified(PropuestaPeer::SUMD)) $criteria->add(PropuestaPeer::SUMD, $this->sumd);
		if ($this->isColumnModified(PropuestaPeer::IS_ACTIVE)) $criteria->add(PropuestaPeer::IS_ACTIVE, $this->is_active);
		if ($this->isColumnModified(PropuestaPeer::CREATED_AT)) $criteria->add(PropuestaPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(PropuestaPeer::MODIFIED_AT)) $criteria->add(PropuestaPeer::MODIFIED_AT, $this->modified_at);
		if ($this->isColumnModified(PropuestaPeer::VANITY)) $criteria->add(PropuestaPeer::VANITY, $this->vanity);

		return $criteria;
	}

